package com.fanxing.server.cache.redis;

/**
 * Redis主机信息
 * @author 	fuhuiyuan
 */
public class RedisHost {
	
	/**主机名称*/
    private String host;
    /**端口号*/
    private Integer port;

    public RedisHost(String host, Integer port) {
        this.host = host;
        this.port = port;
    }

    public String getHost() {
        return host;
    }

    public Integer getPort() {
        return port;
    }

	@Override
	public String toString() {
		return "Host : " + getHost() + " ; prot : " + getPort() + " ;";
	}
    
}
